function levelrange(start, end){
	return {
		// switching from ASCII to UTF-8 encoding because ASCII didn't work with browserify
		//keyEncoding:'ascii',
	    //end:end + '\xff'
	    keyEncoding:'utf-8',    
	    end:end + '\udbff\udfff',
		start:start
	}
}

function querykeys(path, starttime, endtime){
	var start = path;
	var end = path;
	if(starttime){
		start += '.' + starttime;
	}
	if(endtime){
		end += '.' + endtime;
	}
	return levelrange(start, end);
}

module.exports = {
	querykeys:querykeys,
	levelrange:levelrange
}
